import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const NavigationContainer = styled.div`
  background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
  border-bottom: 2px solid #e2e8f0;
  padding: 0;
`;

const NavigationTabs = styled.div`
  display: flex;
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;

  &::-webkit-scrollbar {
    display: none;
  }
`;

const NavigationTab = styled(motion.button)`
  background: ${props => props.active ? 'linear-gradient(135deg, #0f766e, #0d9488)' : 'transparent'};
  color: ${props => props.active ? 'white' : '#64748b'};
  border: none;
  padding: 20px 25px;
  font-size: 0.95rem;
  font-weight: 600;
  cursor: pointer;
  border-bottom: 3px solid ${props => props.active ? '#0f766e' : 'transparent'};
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 10px;
  white-space: nowrap;
  min-width: fit-content;
  position: relative;

  &:hover {
    background: ${props => props.active 
      ? 'linear-gradient(135deg, #0f766e, #0d9488)' 
      : 'rgba(15, 118, 110, 0.1)'};
    color: ${props => props.active ? 'white' : '#0f766e'};
  }

  &:first-child {
    margin-left: 15px;
  }

  &:last-child {
    margin-right: 15px;
  }
`;

const TabIcon = styled.span`
  font-size: 1.1rem;
`;

const TabLabel = styled.span`
  font-weight: 600;
`;

const ActiveIndicator = styled(motion.div)`
  position: absolute;
  bottom: -3px;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(135deg, #0f766e, #0d9488);
  border-radius: 2px 2px 0 0;
`;

const CHCNavigation = ({ sections, activeSection, onSectionChange }) => {
  return (
    <NavigationContainer>
      <NavigationTabs>
        {sections.map((section, index) => (
          <NavigationTab
            key={section.id}
            active={activeSection === section.id}
            onClick={() => onSectionChange(section.id)}
            whileHover={{ y: -2 }}
            whileTap={{ scale: 0.98 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.05 }}
          >
            <TabIcon>{section.icon}</TabIcon>
            <TabLabel>{section.label}</TabLabel>
            {activeSection === section.id && (
              <ActiveIndicator
                layoutId="activeTab"
                transition={{ duration: 0.3, ease: "easeInOut" }}
              />
            )}
          </NavigationTab>
        ))}
      </NavigationTabs>
    </NavigationContainer>
  );
};

export default CHCNavigation;
