import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ImplicationsSection = styled.div`
  background: #1e3a8a;
  color: white;
  padding: 60px 40px;
  position: relative;
`;

const SectionHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 35px;
  padding-bottom: 20px;
  border-bottom: 3px solid #fbbf24;
  width: 100%;
`;

const SectionIcon = styled.div`
  width: 40px;
  height: 40px;
  background: #fbbf24;
  color: #1e3a8a;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  margin-right: 15px;
`;

const SectionTitle = styled.h2`
  font-family: 'Merriweather', serif;
  font-size: 1.6rem;
  font-weight: 700;
  color: white;
  margin: 0;
`;

const ImplicationsList = styled.ul`
  list-style: none;
  margin-top: 40px;
`;

const ImplicationItem = styled(motion.li)`
  background: rgba(255, 255, 255, 0.1);
  padding: 20px 25px;
  margin-bottom: 20px;
  border-radius: 8px;
  border-left: 4px solid #fbbf24;
  position: relative;
  padding-left: 60px;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.15);
    transform: translateX(10px);
  }

  &::before {
    content: "â–¶";
    position: absolute;
    left: 25px;
    color: #fbbf24;
    font-weight: bold;
    font-size: 1.1rem;
  }
`;

const implications = [
  'Medicaid participation is more volatile than previously understood, requiring continuous monitoring and adaptive policies',
  'Community Health Centers should be prioritized in managed care contracting strategies due to their demonstrated stability',
  'Rural areas need targeted workforce retention programs to address higher participation volatility',
  'Provider network adequacy standards should account for participation fluctuations beyond simple provider counts',
  'Proposed Medicaid funding cuts could exacerbate existing participation instability, particularly affecting vulnerable populations'
];

const PolicyImplications = () => {
  return (
    <ImplicationsSection>
      <SectionHeader>
        <SectionIcon>ðŸ“‹</SectionIcon>
        <SectionTitle>Policy Implications</SectionTitle>
      </SectionHeader>

      <ImplicationsList>
        {implications.map((implication, index) => (
          <ImplicationItem
            key={index}
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: index * 0.1 }}
            whileHover={{ scale: 1.02 }}
          >
            {implication}
          </ImplicationItem>
        ))}
      </ImplicationsList>
    </ImplicationsSection>
  );
};

export default PolicyImplications;
