import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeaderContainer = styled.div`
  background: linear-gradient(135deg, #1e3a8a, #3730a3);
  color: white;
  padding: 50px 40px;
  position: relative;
  overflow: hidden;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: 
    radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 1px, transparent 1px),
    radial-gradient(circle at 80% 50%, rgba(255,255,255,0.1) 1px, transparent 1px),
    radial-gradient(circle at 40% 20%, rgba(255,255,255,0.1) 1px, transparent 1px);
  background-size: 60px 60px, 80px 80px, 100px 100px;
  animation: float 20s linear infinite;

  @keyframes float {
    0% { transform: translateY(0px) rotate(0deg); }
    100% { transform: translateY(-20px) rotate(10deg); }
  }
`;

const HeaderContent = styled.div`
  position: relative;
  z-index: 2;
`;

const NihcmLogo = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  margin-bottom: 8px;
  letter-spacing: 3px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const MainTitle = styled.h1`
  font-family: 'Merriweather', serif;
  font-size: 2.8rem;
  font-weight: 400;
  line-height: 1.2;
  margin: 25px 0 15px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const Subtitle = styled.div`
  font-size: 1.2rem;
  opacity: 0.9;
  font-weight: 300;
  margin-bottom: 30px;
`;

const StudyMetrics = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 25px;
  margin-top: 40px;
`;

const MetricCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  border-radius: 15px;
  padding: 25px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  text-align: center;
`;

const MetricNumber = styled.div`
  font-size: 2.5rem;
  font-weight: 700;
  color: #fbbf24;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
`;

const MetricLabel = styled.div`
  font-size: 1rem;
  font-weight: 500;
  line-height: 1.3;
`;

const ComprehensiveHeader = () => {
  const metrics = [
    { number: '220,665', label: 'Primary Care Clinicians Tracked' },
    { number: '40', label: 'States Included in Analysis' },
    { number: '2016-2019', label: 'Longitudinal Study Period' },
    { number: '90%', label: 'Change Threshold for Major Shifts' }
  ];

  return (
    <HeaderContainer>
      <BackgroundPattern />
      <HeaderContent>
        <motion.div
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <NihcmLogo>NIHCM FOUNDATION</NihcmLogo>
          <MainTitle>
            Comprehensive Analysis: Primary Care Clinicians' Medicaid Participation Dynamics
          </MainTitle>
          <Subtitle>
            A Deep Dive into Longitudinal Patterns, Geographic Variations, and Policy Implications
          </Subtitle>
        </motion.div>

        <StudyMetrics>
          {metrics.map((metric, index) => (
            <MetricCard
              key={index}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6, delay: 0.4 + index * 0.1 }}
              whileHover={{ scale: 1.05, y: -5 }}
            >
              <MetricNumber>{metric.number}</MetricNumber>
              <MetricLabel>{metric.label}</MetricLabel>
            </MetricCard>
          ))}
        </StudyMetrics>
      </HeaderContent>
    </HeaderContainer>
  );
};

export default ComprehensiveHeader;
