import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const TabsContainer = styled.div`
  background: #f8fafc;
  border-bottom: 2px solid #e2e8f0;
  padding: 0 20px;
  overflow-x: auto;
`;

const TabsList = styled.div`
  display: flex;
  align-items: center;
  min-width: max-content;
  gap: 5px;
`;

const TabButton = styled(motion.button)`
  background: ${props => props.active ? 'linear-gradient(135deg, #1e3a8a, #3730a3)' : 'transparent'};
  color: ${props => props.active ? 'white' : '#64748b'};
  border: none;
  padding: 18px 24px;
  font-size: 0.95rem;
  font-weight: 600;
  cursor: pointer;
  border-radius: 12px 12px 0 0;
  transition: all 0.3s ease;
  position: relative;
  display: flex;
  align-items: center;
  gap: 8px;
  white-space: nowrap;
  box-shadow: ${props => props.active ? '0 4px 15px rgba(30, 58, 138, 0.3)' : 'none'};

  &:hover {
    background: ${props => props.active ? 'linear-gradient(135deg, #1e3a8a, #3730a3)' : 'rgba(100, 116, 139, 0.1)'};
    transform: translateY(-2px);
  }
`;

const TabIcon = styled.span`
  font-size: 1.2rem;
`;

const TabLabel = styled.span`
  font-family: 'Source Sans Pro', sans-serif;
`;

const ActiveIndicator = styled(motion.div)`
  position: absolute;
  bottom: -2px;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, #fbbf24, #f59e0b);
  border-radius: 3px;
`;

const NavigationTabs = ({ sections, activeSection, onSectionChange }) => {
  return (
    <TabsContainer>
      <TabsList>
        {sections.map((section) => (
          <TabButton
            key={section.id}
            active={activeSection === section.id}
            onClick={() => onSectionChange(section.id)}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <TabIcon>{section.icon}</TabIcon>
            <TabLabel>{section.label}</TabLabel>

            {activeSection === section.id && (
              <ActiveIndicator
                layoutId="activeTab"
                transition={{ type: "spring", bounce: 0.2, duration: 0.6 }}
              />
            )}
          </TabButton>
        ))}
      </TabsList>
    </TabsContainer>
  );
};

export default NavigationTabs;
