import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const ChartContainer = styled(motion.div)`
  background: #f8fafc;
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  padding: 40px;
  margin: 40px 0;
  box-shadow: 0 4px 15px rgba(0,0,0,0.05);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const ChartTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  color: #1e3a8a;
  text-align: center;
  margin-bottom: 30px;
  font-family: 'Merriweather', serif;
  width: 100%;
`;

const ChartWrapper = styled.div`
  width: 100%;
  max-width: 600px;
  height: 400px;
  position: relative;
`;

const BaselineChart = () => {
  const data = {
    labels: ['1-10 Patients', '11-50 Patients', '51-100 Patients', '>100 Patients'],
    datasets: [{
      label: 'Stability Rate (%)',
      data: [45, 55, 65, 75],
      backgroundColor: 'rgba(30, 58, 138, 0.8)',
      borderColor: 'rgba(30, 58, 138, 1)',
      borderWidth: 2,
      borderRadius: 8,
      borderSkipped: false,
      hoverBackgroundColor: 'rgba(30, 58, 138, 0.9)',
      hoverBorderColor: 'rgba(30, 58, 138, 1)',
    }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        backgroundColor: 'rgba(30, 58, 138, 0.9)',
        titleFont: { family: 'Source Sans Pro', weight: 'bold' },
        bodyFont: { family: 'Source Sans Pro' },
        cornerRadius: 8,
        padding: 12,
        callbacks: {
          label: function(context) {
            return `Stability Rate: ${context.parsed.y}%`;
          }
        }
      }
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Baseline Patient Volume',
          font: { 
            family: 'Source Sans Pro', 
            weight: 'bold',
            size: 14
          },
          color: '#374151'
        },
        ticks: {
          font: { family: 'Source Sans Pro' },
          color: '#6b7280'
        },
        grid: {
          display: false
        }
      },
      y: {
        beginAtZero: true,
        max: 100,
        title: {
          display: true,
          text: 'Stability Rate (%)',
          font: { 
            family: 'Source Sans Pro', 
            weight: 'bold',
            size: 14
          },
          color: '#374151'
        },
        ticks: {
          callback: function(value) {
            return value + '%';
          },
          font: { family: 'Source Sans Pro' },
          color: '#6b7280'
        },
        grid: {
          color: 'rgba(107, 114, 128, 0.1)'
        }
      }
    },
    animation: {
      duration: 2000,
      easing: 'easeInOutCubic'
    },
    interaction: {
      intersect: false,
      mode: 'index'
    }
  };

  return (
    <ChartContainer
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8, delay: 0.6 }}
      whileHover={{ scale: 1.01 }}
    >
      <ChartTitle>Stability by Baseline Patient Volume</ChartTitle>
      <ChartWrapper>
        <Bar data={data} options={options} />
      </ChartWrapper>
    </ChartContainer>
  );
};

export default BaselineChart;
